package eval.code.quality;

import java.io.File;
import java.io.FileNotFoundException;

import Tests.BlankLines;
import Tests.Tab;
import Tools.ProcessCU;

public class App {

    public static void main(String[] args) {
      try {
        System.out.println("Test for blank lines result: " + BlankLines.fromFile(new File(args[0])).runTest());
        ProcessCU pcu = ProcessCU.fromFile(new File(args[0]));
        Tab t = new Tab(pcu.getCU());
        t.tabCheck();
      } catch(FileNotFoundException e) {

      }
    } 
}
